import { createClient } from '@supabase/supabase-js';

const supabase = createClient(
  process.env.SUPABASE_URL,
  process.env.SUPABASE_ANON_KEY
);

// Create table (run in Supabase SQL editor first):
// CREATE TABLE users (id uuid DEFAULT gen_random_uuid() PRIMARY KEY, name text, email text);

// Insert
await supabase.from('users').insert({ name: 'Alice', email: 'alice@example.com' });

// Query
const { data } = await supabase.from('users').select('*').eq('name', 'Alice');
console.log(data);

import { S3Client, PutObjectCommand, GetObjectCommand } from '@aws-sdk/client-s3';
import { getSignedUrl } from '@aws-sdk/s3-request-presigner';
import { readFileSync } from 'fs';

const s3 = new S3Client({
  region: 'auto',
  endpoint: `https://${process.env.R2_ACCOUNT_ID}.r2.cloudflarestorage.com`,
  credentials: {
    accessKeyId: process.env.R2_ACCESS_KEY_ID,
    secretAccessKey: process.env.R2_SECRET_ACCESS_KEY
  }
});

// Upload
await s3.send(new PutObjectCommand({
  Bucket: 'my-bucket',
  Key: 'uploads/file.png',
  Body: readFileSync('local_file.png')
}));

// Generate download URL
const url = await getSignedUrl(s3, new GetObjectCommand({
  Bucket: 'my-bucket', Key: 'uploads/file.png'
}), { expiresIn: 3600 });
console.log(url);

