import { Inngest } from 'inngest';

const inngest = new Inngest({ id: 'my-app' });

// Define a function
export const processOrder = inngest.createFunction(
  { id: 'process-order' },
  { event: 'order/created' },
  async ({ event, step }) => {
    // Step 1: Charge payment
    const charge = await step.run('charge-payment', async () => {
      return await chargeCustomer(event.data.customerId, event.data.amount);
    });

    // Step 2: Wait 1 hour, then send confirmation
    await step.sleep('wait-before-email', '1h');

    // Step 3: Send email
    await step.run('send-confirmation', async () => {
      return await sendEmail(event.data.email, charge.id);
    });
  }
);

// Trigger it
await inngest.send({ name: 'order/created', data: { customerId: '123', amount: 2000, email: 'user@example.com' } });

